import React, { useEffect, useState } from 'react';
// import Mekamount, { runScript } from './createMekamount.js';
import querystring from 'query-string';
import twitterLogo from './assets/twitter-logo.svg';
import download from 'downloadjs';
import './App.css';

// Constants
const TWITTER_HANDLE = 'CoachChuckFF';
const TWITTER_LINK = `https://twitter.com/${TWITTER_HANDLE}`;

const TEST_GIFS = [
	'https://i.giphy.com/media/eIG0HfouRQJQr1wBzz/giphy.webp',
	'https://media3.giphy.com/media/L71a8LW2UrKwPaWNYM/giphy.gif?cid=ecf05e47rr9qizx2msjucl1xyvuu47d7kf25tqt2lvo024uo&rid=giphy.gif&ct=g',
	'https://media4.giphy.com/media/AeFmQjHMtEySooOc8K/giphy.gif?cid=ecf05e47qdzhdma2y3ugn32lkgi972z9mpfzocjj6z1ro4ec&rid=giphy.gif&ct=g',
	'https://i.giphy.com/media/PAqjdPkJLDsmBRSYUp/giphy.webp'
]



const App = () => {
  // State
  const [walletAddress, setWalletAddress] = useState(null);
  const [mekAddress, setMekAddress] = useState(null);
  const [pfpAddress, setPfpAddress] = useState(null);
  const [pfpScale, setPfpScale] = useState(0.15);
  const [isBuilding, setIsBuilding] = useState(false);
  const [isGettingNFTS, setIsGettingNFTs] = useState(false);
  const [inputValue, setInputValue] = useState('');
  const [gifList, setGifList] = useState([]);
  const [nftList, setNftList] = useState([]);

  const getURLData = (baseURL = '', path = '', params = {}) => {
    let requestedURL = baseURL + path + ((params.length == 0) ? "" : "?" + querystring.stringify(params));
    return new Promise((resolve, reject) => {
      fetch(requestedURL, {
        method: 'GET',
        cache: 'no-cache',
        headers: {'accept': 'application/json'},
        redirect: 'follow',
        referrerPolicy: 'no-referrer',
      }).then((response) => {
        response.json().then((data)=>{
          resolve(data);
        })
        .catch((error) => {reject(`Could not get JSON (${requestedURL})`);});
      })
      .catch((error) => {reject(`Could not get URL (${requestedURL})`);});
    });
  }

  const grabAllNFTs = () => {
    if(!isGettingNFTS){
      setIsGettingNFTs(true);
      getURLData(
        "https://public-api.solscan.io",
        "/account/tokens",
        { account: `${walletAddress}`,},
      ).then((data) => {

        //Grab NFT Tokens
        let promises = [];
        data.forEach(token => {
          if(token.tokenAmount != null && token.tokenAmount.amount == 1 && token.tokenAmount.decimals == 0){
            promises.push(
              getURLData(
                "https://public-api.solscan.io",
                `/account/${token.tokenAddress}`,
              )
            );
          }
        });

        // Get Metadata on all
        Promise.all(promises)
        .then((nfts)=>{

          //Grab only the useful stuff
          let nftMetadata = [];
          console.log(nfts[0]);
          nfts.forEach(nft=> {
            if(nft.metadata.data.image != null){
              nftMetadata.push(
                {
                  name : nft.metadata.data.name, 
                  collection : nft.metadata.data.symbol, 
                  url : nft.metadata.data.image,
                  address : nft.account,
                }
              );
            }
          });

          //Sort
          nftMetadata.sort((a, b)=>{
            return a.collection.localeCompare(b.collection);
          });

          //Update
          setNftList(nftMetadata);
        })
        .catch((error) => {
          alert('Could not grab ALL NFTs');
          setIsGettingNFTs(false);
        })

      })
      .catch((error) => {
        alert('Could not grab NFTs');
        setIsGettingNFTs(false);
      })
    } else {
      // alert('Crawling...');
      console.log("Crawling...")
    }
  }

  const downloadNewMek = async () => {
    if(!isBuilding){
      setIsBuilding(true);
      setMekAddress("5B1QZJYws1Nnp8Kh3FWVoeQbasr5tJeyiZZnWz8sxDZf");
      // setMekAddress("B1QZJYws1Nnp8Kh3FWVoeQbasr5tJeyiZZnWz8sxDZf");
      setPfpAddress("CsTRmLYi8Vgwqwc8fbRG6prAwUakpZRErzp1zXBHZamc");
      try {
        const response = await fetch(`/sol/${walletAddress}/meka/${mekAddress}/pfp/${pfpAddress}/scale/${pfpScale}`);
        const blob = await response.blob();

        if(blob.size < 500){
          const data = await response.json();
          if(data.error != null){
            alert(data.error);
          } else {
            alert('Error merging NFTs');
          }
        }

      } catch (error) {
        alert('Error merging NFTs');
      }

      setIsBuilding(false);
    } else {
      alert('Building...');
    }
  };


  const renderConnectedContainer = () => (
    <div className="connected-container">
      <form
        onSubmit={(event) => {
          event.preventDefault();
          sendGif();
        }}
      >
        <input
          type="text"
          placeholder="Enter gif link!"
          value={inputValue}
          onChange={onInputChange}
        />
        <button type="submit" className="cta-button submit-gif-button" onClick={downloadNewMek}>
          Submit
        </button>
      </form>
      <div className="gif-grid">
        {/* Map through gifList instead of TEST_GIFS */}
        {nftList.map((nft) => (
          <div className="gif-item" key={nft.url}>
            <img src={nft.url} alt={nft.url} />
            <p>{nft.name}</p>
          </div>
        ))}
      </div>
    </div>
  );

  // Actions
  const checkIfWalletIsConnected = async () => {
    try {
      const { solana } = window;

      if (solana) {
        if (solana.isPhantom) {
          console.log('Phantom wallet found!');
          const response = await solana.connect({ onlyIfTrusted: true });
          console.log(
            'Connected with Public Key:',
            response.publicKey.toString()
          );

          setWalletAddress(response.publicKey.toString());
        }
      } else {
        alert('Solana object not found! Get a Phantom Wallet ðŸ‘»');
      }
    } catch (error) {
      console.error(error);
    }
  };

  const connectWallet = async () => {
    const { solana } = window;

    if (solana) {
      const response = await solana.connect();
      console.log('Connected with Public Key:', response.publicKey.toString());
      setWalletAddress(response.publicKey.toString());
    }
  };

  const onInputChange = (event) => {
    const { value } = event.target;
    setInputValue(value);
  };

  const sendGif = async () => {
    if (inputValue.length > 0) {
      console.log('Gif link:', inputValue);
      setGifList([...gifList, inputValue]);
      setInputValue('');
    } else {
      console.log('Empty input. Try again.');
    }
  };

  const renderNotConnectedContainer = () => (
    <button
      className="cta-button connect-wallet-button"
      onClick={connectWallet}
    >
      Connect to Wallet
    </button>
  );

  // UseEffects
  useEffect(() => {
    const onLoad = async () => {
      await checkIfWalletIsConnected();
    };
    window.addEventListener('load', onLoad);
    return () => window.removeEventListener('load', onLoad);
  }, []);

  useEffect(() => {
    if (walletAddress) {
      console.log('Fetching Mekamounts...');

      // Call Solana program here.
      grabAllNFTs();

      // Set state
      // setGifList(TEST_GIFS);
    }
  }, [walletAddress]);

  return (
    <div className="App">
			{/* This was solely added for some styling fanciness */}
			<div className={walletAddress ? 'authed-container' : 'container'}>
        <div className="header-container">
          <p className="header">ðŸ¦¾ Mekamount PFP Portal [Unoffical]</p>
          <p className="sub-text">
            N F T âœ¨
          </p>
          {!walletAddress && renderNotConnectedContainer()}
          {/* We just need to add the inverse here! */}
          {walletAddress && renderConnectedContainer()}
        </div>
        <div className="footer-container">
          <img alt="Twitter Logo" className="twitter-logo" src={twitterLogo} />
          <a
            className="footer-text"
            href={TWITTER_LINK}
            target="_blank"
            rel="noreferrer"
          >{`Crafted By @${TWITTER_HANDLE}`}</a>
        </div>
      </div>
    </div>
  );
};

export default App;
